#/bin/bash
        sed -i "s/#newanchorpeer/    - \&pnb\n        # DefaultOrg defines the organization which is used in the sampleconfig\n        # of the fabric.git development environment\n        Name: pnbMSP\n        # ID to load the MSP definition as\n        ID: pnbMSP\n        MSPDir: crypto-config\/peerOrganizations\/pnb.bankconsortiumbcnet.com\/msp\n        AnchorPeers:\n            # AnchorPeers defines the location of peers which can be used\n            # for cross org gossip communication.  Note, this value is only\n            # encoded in the genesis block in the Application section context\n            - Host: peer0.pnb.bankconsortiumbcnet.com\n              Port: 7121\n#newanchorpeer/g" configtx.yaml
sed -i "s/#neworg/elif [ \$ORG == \"pnb\" ] ; then\n          set -x\n          CORE_PEER_LOCALMSPID=\"pnbMSP\"\n          CORE_PEER_TLS_ROOTCERT_FILE=\/opt\/gopath\/src\/github.com\/hyperledger\/fabric\/peer\/crypto\/peerOrganizations\/pnb.bankconsortiumbcnet.com\/peers\/peer0.pnb.bankconsortiumbcnet.com\/tls\/ca.crt\n          CORE_PEER_MSPCONFIGPATH=\/opt\/gopath\/src\/github.com\/hyperledger\/fabric\/peer\/crypto\/peerOrganizations\/pnb.bankconsortiumbcnet.com\/users\/Admin@pnb.bankconsortiumbcnet.com\/msp\n          set +x\n          if [ \$PEER -eq 0 ]; then\n            set -x\n            CORE_PEER_ADDRESS=peer0.pnb.bankconsortiumbcnet.com:7121\n            set +x\n#newpeerpnb/g" ./scripts/utils.sh
sed -i "s/#newpeerpnb/          elif [ \$PEER -eq 1 ]; then\n            set -x\n            CORE_PEER_ADDRESS=peer1.pnb.bankconsortiumbcnet.com:7121\n            set +x\n#newpeerpnb\n          fi\n#neworg/g" ./scripts/utils.sh
########################## updateing new org details (orgname and ports of peers) in orgsports.json #########################################
python operations.py addPortDetail org '{ "orgId" : "pnb", "peer" : [{ "peerName" : "peer0" , "port" : "7121" }, { "peerName" : "peer1" , "port" : "7123" }]}'
